mapper.select:
SELECT od.order_id AS orderId, od.payment_time AS paymentTime, cu.nick_name AS nickName, cu.head_portrait_url AS headPortraitUrl, p.`name` AS productName, p.price AS price, p.product_id AS productId, od.order_num AS orderNum, od.order_amounts AS orderAmounts, od.order_status AS orderStatus, DATE_FORMAT(od.start_time,'%Y-%m-%d %H:%i:%s') AS startTimeStr
FROM orders od LEFT JOIN customer cu ON od.buyer_id = cu.customer_id LEFT JOIN product p ON od.product_id = p.product_id WHERE od.seller_id = #{customerId}
AND od.order_status > 3 order by od.order_time desc
