mapper.select:
SELECT

FROM activity_record ORDER BY create_time ASC

  LIMIT #{begin},#{last}

SELECT

FROM activity_record 
where activity_id=#{activityId}
and user_id= #{userId}
ORDER BY id desc
limit 1

SELECT
*
FROM activity_record 
where user_id=#{userId}
and activity_id=#{activityId}
and other_id=#{otherId}
and recharge_amount is not null
limit 1

SELECT
  sum(award_amount)
  FROM activity_record 
  where user_id=#{userId}
  and activity_id=#{activityId}

SELECT  *  from activity_record 
 where DATE_FORMAT(create_time,'%Y-%m-%d')=#{time} 


   and  record='充值限时抢活动-抢' 


   and  record='充值优惠限时抢-充值赠送' 


  and record='除夕活动-抽取红包'


 and user_id=#{userId}
