mapper.select:
select count(1)
 from customer_coupon
 where customer_id = #{customerId,jdbcType=BIGINT}
  and coupon_id = #{couponId,jdbcType=BIGINT}

select c.coupon_name as couponName ,a.activity_name as activityName from coupon c ,activity a
where c.activity_id = a.activity_id
and c.coupon_id = #{couponId}

        select *
        from customer_coupon
        where is_used = 0
		and coupon_id = #{couponId}
        and customer_id = #{customerId}
