mapper.select:
select count(apply_id)
from customer_apply_bigv apply
INNER JOIN customer cus ON apply.customer_id=cus.customer_id
where 1=1
 and (cus.customer_id = #{name} or cus.nick_name like concat('%',#{name},'%') )
 and apply.handle_status = #{handleStatus}
 and apply.apply_time  >=  #{startTime}
 and apply.apply_time  <=  #{endTime}

select

from customer_apply_bigv apply
INNER JOIN customer cus ON apply.customer_id=cus.customer_id
where 1=1
 and (cus.customer_id like concat('%',#{name},'%') or cus.nick_name like concat('%',#{name},'%') )
 and apply.handle_status = #{handleStatus}
 and apply.apply_time  >=  #{startTime}
 and apply.apply_time  <=  #{endTime}
order by apply_time desc
limit ${iDisplayStart},${iDisplayLength}
