mapper.select:
select count(1) from message

  
    and send_status = #{sendStatus}
  
  
    and type = #{type}
  
  
     and create_time >= #{startTime} 
  
  
     and create_time <= #{endTime}

select

from message

  
    and send_status = #{sendStatus}
  
  
    and type = #{type}
  
  
     and create_time >= #{startTime} 
  
  
     and create_time <= #{endTime} 
  

order by create_time desc
limit ${iDisplayStart},${iDisplayLength}
