mapper.select:
select count(0) from user_install_info  where 1=1
   and is_gjplatform = #{isGjplatform}
     and account_id = #{accountId}

		select count(app_name)AS account_sum,app_name,is_gjplatform  from user_install_info   WHERE  1=1 

		 and app_name =#{appName}
         and is_gjplatform = #{isGjplatform}
        group by app_name ,is_gjplatform

	SELECT COUNT(DISTINCT app_name,is_gjplatform)  FROM user_install_info   WHERE  1=1  
		and app_name =#{appName}
         and is_gjplatform = #{isGjplatform}

      SELECT COUNT(DISTINCT(account_id)) FROM user_install_info 
      WHERE  (TO_DAYS(NOW())- TO_DAYS(first_time)) < 7
and app_name = #{appName}

      select count(user_install_id) from user_install_info where 1=1
         and phone_num =#{phoneNum}
       	and app_name =#{appName}
         and is_gjplatform = #{isGjplatform}
         and create_time >= #{startTime}
		 and create_time <= #{endTime}

		select

		from user_install_info where 1=1
		 and phone_num =#{phoneNum}
      	and app_name =#{appName}
         and is_gjplatform = #{isGjplatform}
         and create_time >= #{startTime}
		 and create_time <= #{endTime}
		 order by  ${orderStr} 
		limit ${iDisplayStart},${iDisplayLength}
