mapper.select:
 select

 from activity_award
	where account_id= #{accountId}
and ref_account_id=#{refAccountId}
and activity_id=#{activityId}

 select

 from activity_award
	where account_id= #{accountId}
and remark=#{remark}
and activity_id=#{activityId}

select count(activity_award_id) from activity_award where 1=1
 and phone_num = #{phoneNum}
 and ref_phone_num = #{refPhoneNum}
 and remark like concat('%',#{remark},'%')
 and state = #{state}
 and activity_id = #{activityId}
	 and create_time >= #{startTime}
		 and create_time <= #{endTime}

    select

		from activity_award where 1=1
		 and phone_num = #{phoneNum}
		 and ref_phone_num = #{refPhoneNum}
		 and remark like concat('%',#{remark},'%')
		 and state = #{state}
			 and activity_id = #{activityId}
		 order by  ${orderStr}
		 and create_time >= #{startTime}
		 and create_time <= #{endTime}
		limit ${iDisplayStart},${iDisplayLength}

   SELECT aw.* ,act.title as activityTitle FROM  activity_award aw LEFT JOIN activity act ON aw.activity_id = act.activity_id

          where 1=1
		 and aw.phone_num = #{phoneNum}
		 and aw.ref_phone_num = #{refPhoneNum}
		 and aw.remark like concat('%',#{remark},'%')
		 and act.title like concat('%',#{activityTitle},'%')
		 and aw.state = #{state}
			 and aw.activity_id = #{activityId}
			 and aw.create_time >= #{startTime}
				 and aw.create_time <= #{endTime}
		 order by  ${orderStr}
		limit ${iDisplayStart},${iDisplayLength}

    select

		from activity_award where 1=1
		 and phone_num = #{phoneNum}
		 and ref_phone_num = #{refPhoneNum}
		 and remark like concat('%',#{remark},'%')
		 and state = #{state}
			 and activity_id = #{activityId}
		 order by  ${orderStr}

		select gift_info_id giftInfoId,gift_name giftName,odds percent,amount giveNum,state,create_time createTime,create_man createMan,modify_time modifyTime,modify_man modifyMan ,remark from gift_info


     	 gift_name= #{title,jdbcType=VARCHAR}


     	and state = #{state}


		ORDER BY create_time desc
		limit ${iDisplayStart},${iDisplayLength}

		select count(*) from gift_info


        	 gift_name= #{title,jdbcType=VARCHAR}


        	and state = #{state}
