mapper.select:
select 

from skill
where  1 = 1
 
    and  skill_status = #{skillStatus}
 
 
    and  name like  concat( '%',#{name},'%')
 
 LIMIT ${iDisplayStart},${iDisplayLength}

select count(1) from skill where 1= 1
and skill_status = #{skillStatus}
and name like concat( '%',#{name},'%')
