mapper.update:
    
		UPDATE 
			orders
		SET 
			system_cancel_time = #{currTime},
		 	order_status = #{updateStatus}
		WHERE
			order_status = #{queryStatus}
		AND order_time    <  #{endTime}

    
		UPDATE 
			orders
		SET 
			system_cancel_time = #{currTime},
		 	order_status = #{updateStatus}
		WHERE
			order_status = #{queryStatus}
		AND receive_order_time  <  #{endTime}

    
		UPDATE 
			orders
		SET 
			system_cancel_time = #{currTime},
		 	order_status = #{updateStatus}
		WHERE
			order_status = #{queryStatus}
		AND start_time  <  #{endTime}

    
		UPDATE 
			orders
		SET 
			system_cancel_time = #{currTime},
		 	order_status = #{updateStatus}
		WHERE
			order_status = #{queryStatus}
		AND start_time  <  #{endTime}

    
		UPDATE 
			orders
		SET 
		 	order_status = #{updateStatus}
		WHERE
			order_status = #{queryStatus}
		AND appoint_time  >  #{endTime}
		AND  skill_type  = #{skillType}

    
		UPDATE 
			orders
		SET 
			end_time = #{currTime},
		 	order_status = #{updateStatus}
		WHERE
			order_status = #{queryStatus}
		AND expect_end_time  >  #{endTime}
		AND  skill_type  = #{skillType}
