mapper.select:
select customer_id AS customerId, app_id AS appId, nick_name AS nickName, head_portrait_url AS headPortraitUrl, sex, (case when v_status=2 then 1 else 0 end ) as type, (YEAR (NOW()) - YEAR (birthday)) AS age
,IFNULL(fs.attention_state,0) AS attentionStatus, (IFNULL(fs.attention_state,0))&(IFNULL(fs1.attention_state,0)) AS eachAttentionStatus
from customer cus
LEFT JOIN fans fs ON fs.anchor_id = cus.customer_id and fs.fans_id = #{customerId} LEFT JOIN fans fs1 ON fs.anchor_id = fs1.fans_id and fs1.anchor_id = fs.fans_id
app_id = #{keyword}
and customer_id != #{customerId}

select 

from customer
where 1=1

and phone=#{phone}


and cust_password=#{custPassword}


and wechat_open_id=#{wechatOpenId}


and qq_open_id=#{qqOpenId}


and microblog_open_id=#{microblogOpenId}


and nick_name=#{nickName}

mapper.update:
update customer set head_portrait_url=#{headPortraitUrl},nick_name=#{nickName},
sex=#{sex}
where phone=#{phone}

mapper.insert:
insert into customer_apply_bigv (apply_id, customer_id, apply_time)
SELECT #{applyId}, #{customerId}, #{applyTime}
FROM DUAL WHERE NOT EXISTS (SELECT customer_id FROM customer_apply_bigv WHERE customer_id = #{customerId})
