mapper.select:
select * from ( select customer_id AS customerId, nick_name AS nickName, head_portrait_url AS headPortraitUrl, sex, type, (YEAR(CURDATE())-YEAR(birthday))-(RIGHT(CURDATE(),5)<RIGHT(birthday,5)) AS age from customer
nick_name LIKE concat('%',#{keyword},'%')
and customer_id != #{customerId}
order by (case when nick_name = #{keyword} then 1 when nick_name like concat(#{keyword},'%') then 2 when nick_name like concat('%',#{keyword}) then 3 when nick_name like concat('%',#{keyword},'%') then 4 else 0 end ) ) cus_temp
LIMIT #{pageIndex},#{pageSize}

select customer_id AS customerId, nick_name AS nickName, head_portrait_url AS headPortraitUrl, sex, type, (YEAR(CURDATE())-YEAR(birthday))-(RIGHT(CURDATE(),5)<RIGHT(birthday,5)) AS age from customer
app_id = #{keyword}
and customer_id != #{customerId}

select 

from customer
where 1=1

and phone=#{phone}


and cust_password=#{custPassword}


and wechat_open_id=#{wechatOpenId}


and qq_open_id=#{qqOpenId}


and microblog_open_id=#{microblogOpenId}

mapper.update:
update customer set head_portrait_url=#{headPortraitUrl},nick_name=#{nickName},
sex=#{sex}
where phone=#{phone}
