mapper.select:
SELECT t.customer_id, t.customer_skill_id, t.skill_item_id, t.skill_name, t.discount_price, t.service_unit, t2.skill_voice_url, t2.skill_voice_time, t3.back_color as skill_back_color, t3.home_black_color as skill_home_black_color, t3.font_color as skill_font_color, t4.nick_name as customer_nick_name, t4.sex as customer_sex, t4.birthday as customer_birthday FROM customer_skill t LEFT JOIN customer_skill_certify t2 ON t2.certify_id = t.certify_id LEFT JOIN skill_item t3 ON t3.id = t.skill_item_id LEFT JOIN customer t4 ON t4.customer_id = t.customer_id WHERE 1=1
AND t.skill_item_id = #{skillItemId}
AND t.skill_status = 1 ORDER BY t.create_time ASC limit #{pageIndex}, #{pageSize}

select
skill_item_id
from customer_skill
where customer_id = #{customerId,jdbcType=BIGINT}
limit 1;

select * from( SELECT bss.customer_id as customerId, bss.customer_skill_id as customerSkillId, bss.skill_item_id as skillItemId, csk.skill_name as skillItemName, csk.discount_price as price, csk.service_unit as unitName, csc.skill_voice_url as voiceUrl, csc.skill_voice_time as voiceTime, si.back_color as backColor, si.home_black_color as skillHomeBackColor, si.font_color as skillFontColor, c.nick_name as nickName, c.sex as sex, (YEAR (NOW()) - YEAR (c.birthday)) AS age, bss.score_total ,(SELECT count(o.order_id) FROM orders o WHERE o.customer_skill_id = bss.customer_skill_id AND o.skill_type = (select si.skill_type from skill_item si where si.id = bss.skill_item_id) AND o.order_status IN (10, 18, 26, 28) ) AS skillOrdered FROM bigv_skill_score bss INNER JOIN customer_skill csk ON csk.customer_skill_id = bss.customer_skill_id AND csk.skill_status = 1
LEFT JOIN customer_skill_certify csc ON csc.certify_id = csk.certify_id LEFT JOIN skill_item si ON si.id = bss.skill_item_id LEFT JOIN customer c ON c.customer_id = bss.customer_id WHERE 1=1
and bss.skill_item_id = #{skillItemId}
ORDER BY skillOrdered ASC, bss.score_total DESC ) tmp
LIMIT #{start},#{size}

select count(1)
 from orders
 where customer_id = #{customerId,jdbcType=BIGINT}
 
  and customer_skill_id = #{customerSkillId,jdbcType=BIGINT}
