mapper.select:
SELECT tt.*, csc.skill_voice_url AS skillVoiceUrl, csc.skill_voice_time AS skillVoiceTime FROM( SELECT pc.customer_id AS customerId, c.nick_name AS customerNickName, c.sex AS customerSex, c.birthday AS customerBirthday, csk.customer_skill_id AS customerSkillId, csk.skill_item_id AS skillItemId, csk.discount_price AS discountPrice, csk.service_unit AS serviceUnit, si.skill_item_name AS skillName, si.back_color AS skillBackColor, si.home_black_color AS skillHomeBackColor, si.font_color AS skillFontColor, csk.certify_id FROM resource_pool_customer pc INNER JOIN customer c ON c.customer_id = pc.customer_id AND c.cust_state = 2 AND c.cust_status = 1 AND c.v_status = 2 INNER JOIN customer_skill csk ON csk.customer_id = pc.customer_id AND csk.skill_status = 1 AND csk.skill_item_id IN
#{configSkill}
AND csk.switch_status = 1 AND csk.receive_status = 1
INNER JOIN skill_item si ON si.id = csk.skill_item_id AND si.skill_status = 1 WHERE pc.resource_pool_id = #{resourcePoolId}
and pc.customer_id NOT IN
#{customerId}
AND pc.`status` = 1
AND (SELECT count(o.order_id) FROM orders o WHERE o.customer_skill_id = csk.customer_skill_id AND o.skill_type = (select si.skill_type from skill_item si where si.id = csk.skill_item_id) AND o.order_status IN (10, 18, 26, 28)) = 0
) tt LEFT JOIN customer_skill_certify csc ON csc.certify_id = tt.certify_id ORDER BY RAND() LIMIT 1
